import React, { useState, useEffect } from 'react';
function NewPost({ onAddPost }) {
    const [title, setTitle] = useState('');
    const [content, setContent] = useState('');

    const handleSubmit = async(e) => {
        e.preventDefault();
        const requestOptions = {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ title, content }) // Send title and content in JSON format
        };

        try {
            const response = await fetch('http://localhost:3001/api/posts', requestOptions);
            const data = await response.json();
            onAddPost(data); // Update posts state with the newly added post
            setTitle('');
            setContent('');
        } catch (error) {
            console.error('Error adding post:', error);
        }

    };

    return (
    <div>
        <h2>Add a New Post</h2>
        <form onSubmit={handleSubmit}>
            <div>
                <label>Title:</label>
                <input type="text" value={title} onChange={(e) => setTitle(e.target.value)} />
            </div>

            <div>
                <label>Content:</label>
                <textarea value={content} onChange={(e) => setContent(e.target.value)}></textarea>
            </div>
        <button type="submit">Add Post</button>
        </form>
    </div>
    );
}
export default NewPost;
