import React, { useState, useEffect } from 'react';

//Will contain the form to write and post a new blog
function NewPost({ onAddPost }) {
    const [title, setTitle] = useState('');
    const [content, setContent] = useState('');
    const [showPopup, setShowPopup] = useState('');

    const handleSubmit = (e) => {
        e.preventDefault();
        const newPost = { id: Date.now(), title, content };
        onAddPost(newPost); // This function would be passed down from a parent component or context
        setTitle('');
        setContent('');

        const requestOptions = {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify(newPost)
        };

        fetch('http://localhost:3001/api/posts', requestOptions)
        .then(response => {
            if (response.ok) {
                setShowPopup('Post was successful!');
                setTimeout(() => setShowPopup(''), 3000);
            } else {
                setShowPopup(response.text());
                setTimeout(() => setShowPopup(''), 3000);
            }
        })
        .then(data => {
            onAddPost(data);
            setTitle('');
            setContent('');
        })

    };

    return (
    <div className="newPost">
        <h2>Add a New Post</h2>
        <form onSubmit={handleSubmit}>
            <div>
                <label>Title:</label>
                <input type="text" value={title} onChange={(e) => setTitle(e.target.value)} />
            </div>

            <div>
                <label>Content:</label>
                <textarea value={content} onChange={(e) => setContent(e.target.value)}></textarea>
            </div>
